// TODO:
// - sistemare autori & categoria con lodash [provare con un for in jsx]

import React, { useState, useEffect } from "react";
import _ from "lodash";
import axios from "axios";

// const bookAuthors = (authors) => {
//   if (!authors) return "";
//   if (authors.length <= 2) {
//     authors = authors.join(" and ");
//   } else if (authors.length > 2) {
//     let lastAuthor = " and " + authors.slice(-1);
//     authors.pop();
//     authors = authors.join(", ");
//     authors += lastAuthor;
//   }
//   return authors;
// };

const BooksDetail = ({ match }) => {
  const [book, setBook] = useState({});
  const [error, setError] = useState(false);

  // console.log(match);

  // API
  let API_URL = `https://www.googleapis.com/books/v1/volumes`;

  useEffect(() => {
    const fetchBook = async () => {
      setError(false);
      try {
        const fetchItem = await axios.get(`${API_URL}?q=${match.params.id}`);
        setBook(fetchItem.data.items[0]);
        console.log(fetchItem.data.items[0]);
      } catch (error) {
        setError(true);
      }
    };

    fetchBook();
    console.log();
  }, []);

  return (
    <div>
      <p>Ciao, perchè fa così?</p>
      {error && (
        <div style={{ color: `red` }}>
          some error occurred, while fetching api
        </div>
      )}

      <img
        alt={_.get(book, "id", "")}
        src={_.get(book, "volumeInfo.imageLinks.thumbnail", "")}
      />
      <h3>{_.get(book, "volumeInfo.title", "Error")}</h3>
      <p>{_.get(book, "volumeInfo.subtitle", "Error")}</p>

      <p>{_.get(book, "volumeInfo.description", "No")}</p>

      <p>
        Autore/i:{" "}
        {_.get(book, "volumeInfo.authors[0]", "Gli autori non sono noti")}{" "}
        {_.get(book, "volumeInfo.authors[1]", "")}{" "}
        {_.get(book, "volumeInfo.authors[2]", "")}
      </p>

      <p>{_.get(book, "searchInfo.textSnippet", "")}</p>
      <p>
        Categoria:{" "}
        {_.get(book, "volumeInfo.categories[0]", "Non è stato categorizzato")}{" "}
        {_.get(book, "volumeInfo.categories[1]", "")}{" "}
        {_.get(book, "volumeInfo.categories[2]", "")}
      </p>
      <p>Lingua: {_.get(book, "volumeInfo.language", "Error")}</p>
      <p>
        Anno di pubblicazione:{" "}
        {_.get(book, "volumeInfo.publishedDate", "Error")}
      </p>
      <p>
        Prezzo: {_.get(book, "saleInfo.listPrice.amount", "Non disponibile")}{" "}
        {_.get(book, "saleInfo.listPrice.currencyCode", "")}
      </p>
    </div>
  );
};

export default BooksDetail;
